import React, {Component} from "react";

export default class Counter extends Component{
    constructor(props){
        super(props)
        this.state= {
            count: 0,
            message: ""
        }
    }
      getCount = () => {
        this.setState({
            count : this.state.count + 1
        
        })
        
    
      }

      getMessage(){
        this.setState({
            message:"How are you Shivam"
        
        })
      }

    render(){
        return(
            <div>
            <div>Click {this.state.count} it will get increase</div>
            <div>{this.state.message}</div>
            <button onClick={()=>this.getCount}>Click</button>
            <br></br>
            <button onClick={()=>this.getMessage}>Message</button>
            </div>
            
        );
    }

}
