import React, {Component} from "react";

export default class Counter extends Component{
    constructor(props){
        super(props)
        this.state= {
            count: 0,
            message: "Hello"
        }
    }
      getCount = () => {
        this.setState({
            count : this.state.count + 1
        
        })
        
    
      }

    render(){
        return(
            <div>
            <h1>You Clicked {this.state.count} times</h1>
           <button onClick={this.getCount}>Click</button>
           </div>
            
        );
    }

}
