import React, { useState } from "react";
import ParentHook from "../components/ParentHook";
import './style.css';
import { Link } from "react-router-dom";

export default function ToDoList(){
    const[items, setItems] = useState([])
    const [ userInput, setUserInput ] = useState('');
    

    const handleChange = (e) => {
        setUserInput(e.currentTarget.value)
    }

    const addItem = () => {
        setItems([ ...items, {
            id: items.length+1,
            task: userInput

        }

        ])

    }
        
        
        
      const Lists = items.map(items => ( <li key={items.id}>{items.id}{items.task}</li>))

    return(
    <React.Fragment>
        <div className="sidebar">
    <Link to="/">Home</Link>
    <Link to="/counter">Click Game</Link>
    <Link to="/todolist" className="active">To Do List</Link>
    <a href="#about">Contact</a>
    </div>
    <div className="todo">
    <h1 className="heading">To Do List App</h1>
    
        <input type="text" value={userInput} onChange={handleChange}></input> <button onClick={addItem} >Submit</button>
    
    <br></br>
       <ul>
        {Lists}
        </ul>     
        </div>
        </React.Fragment>
    )
}
