import React, { useState } from "react";
import { Link } from "react-router-dom";
import './Style.css';

export default function Random(){
    const [value, setValue]= useState("red");

    const handling = () =>{
        setValue({
            color:"blue"
        })
    }
    return(
        
            <React.Fragment>
         
          <div>
              
           <h1>{value}</h1>     
           <button onClick={handling}>click</button>
           </div>
           </React.Fragment>
       
    )
}
