import React, {Component} from "react";

export default class Counter extends Component{
    constructor(props){
        super(props)
        this.state= {
            count: 0
        }
    }
      getCount = () => {
        this.setState({
            count : this.state.count + 1
        })
        console.log(this.state.count);
    
      }
    render(){
        return(
            <div>
            <div>Click {this.state.count} it will get increase</div>
            <button onClick={()=>this.getCount()}>Click</button>

            </div>
            
        );
    }

}
